import React, { useState } from "react";
import "./Choise.css";

const Choise = (props) => {
  const [btnContent,setBtnContent] = useState('Please select tool')
  const [visibile,setVisibility] = useState(false)
  // const update_selected_tool = () => {
  //   let select_bar = document.querySelector("select");
  //   props.update_selected_tool(select_bar.value);
  //   props.rendOnOf(1);
  // };

  const update_selected_tool = ()=>{

  }

  const toggle = ()=>{
    setVisibility(!visibile)
  }

  const vis_stat = ()=>{
    if (visibile)
      return 'block';
    return 'none';
  }

  return (
    <div className='Choise'>
      {/* <select
        className="choise"
        default="Select Tool"
        onChange={update_selected_tool}
      >
        <option value="" disabled="disabled" selected="selected">
          Please select tool
        </option>
        <option value="0">GTO</option>
        <option value="1">GTA</option>
        <option value="2">GTX</option>
        <option value="3">GTT</option>
        <option value="4">GNT</option>
        <option value="5">DS1</option>
        <option value="6">ANT</option>
      </select> */}


      <button className='choose_tool' onClick={toggle}>
          {btnContent}
      </button>
      
      <div style ={{display: vis_stat() }} className='options'>
        <div id='0' className='tool' onClick={(e)=>console.log(typeof(e.target.id))}>
          GTO
        </div>

        <div id='1' className='tool'>
          GTA
        </div>

        <div id='2' className='tool'>
          GTX
        </div>

        <div id='3' className='tool'>
          GTT
        </div>

        <div id='4' className='tool'>
          GNT
        </div>

        <div id='5' className='tool'>
          DS1
        </div>

        <div id='6' className='tool'>
          ANT
        </div>
      </div>


    </div>
  );
};

export default Choise;
