import React, { useState } from "react";
import "./Choise.css";

const Choise = (props) => {
  const [btnContent,setBtnContent] = useState('Please select tool')
  const update_selected_tool = () => {
    let select_bar = document.querySelector("select");
    props.update_selected_tool(select_bar.value);
    props.rendOnOf(1);
  };
  return (
    <div>
      {/* <select
