        <option value="3">GTT</option>
        <option value="4">GNT</option>
        <option value="5">DS1</option>
        <option value="6">ANT</option>
      </select> */}


      <button className='choose_tool'>
          {btn_content}
      </button>


    </div>
  );
};

export default Choise;
