import React from "react";

const Post = (props) => {
  const show_content = () => {
    if (props.rend > 1) {
      return (
        <div>
          {props.rend_list.task_list.map((task) => {
            return (
              <div>
                The task:{task.name}
                <br />
                The spec:{task.spec}
              </div>
            );
          })}
        </div>
      );
    }
  };

  if (props.rend === 0) {
    return <div></div>;
  }
  return (
    <div>
      Tool:{props.rend_list.tool}
      <br />
      Spec:{props.rend_list.spec}
      {show_content()}
    </div>
  );
};

export default Post;
